import { Component, ElementRef, EventEmitter, Output, ViewChild } from '@angular/core';
import { Message } from '../message.model';

@Component({
  selector: 'cms-message-edit',
  templateUrl: './message-edit.component.html',
  styleUrls: ['./message-edit.component.css'],
})
export class MessageEditComponent {
  @ViewChild('subject') subjectRef: ElementRef;
  @ViewChild('msgText') msgTextRef: ElementRef;
  @Output() messageAdded = new EventEmitter<Message>();

  onSendMessage() {
    console.log('Send Message');
    const subjectValue = this.subjectRef.nativeElement.value;
    const msgTextValue = this.msgTextRef.nativeElement.value;
    const newMessage = new Message(1, subjectValue, msgTextValue, '1');
    this.messageAdded.emit(newMessage);
  }
  onClear() {
    console.log('Clear Message');
  }

  constructor() {}
}
<div class="panel panel-default">
  <div class="panel-body">
    <form id="document-edit">
      <div class="row">
        <div class="col-sm-12 form-group">
          <label for="subject">Subject</label>
          <input
            type="text"
            id="subject"
            class="form-control"
            size="120"
            max="120"
            #subject
          />
        </div>

        <div class="col-sm-12 form-group">
          <label for="message">Message</label>
          <input
            type="text"
            id="message"
            class="form-control"
            max="255"
            size="120"
            #msgText
          />
        </div>
      </div>
      <div class="row">
        <div class="col-xs-12">
          <button class="btn btn-success" type="button" (click)="onSendMessage()">Send</button>
          <button class="btn btn-danger" type="button" (click)="onClear()">Clear</button>
        </div>
      </div>
    </form>
  </div>
</div>
