import { EventEmitter, Injectable } from "@angular/core";
import { Team } from "./team.model";
import { Subject } from "rxjs";
import { HttpClient } from "@angular/common/http";

@Injectable({
    providedIn: 'root'
})
export class TeamService {
    teams: Team[] = [];
    teamsListChangedEvent = new Subject<Team[]>();
    maxTeamId: number;
    teamSelectedEvent = new EventEmitter<Team>();
    teamChangedEvent = new EventEmitter<Team[]>();
    constructor(
        private http: HttpClient
    ) {
     }

    getTeams() {
        this.http.get<{ message: string, teams: Team[] }>('http://localhost:3000/teams')
        .subscribe(
            (responseData) => {
                this.teams = responseData.teams;
                this.sortAndSend();
            }
        );
    }

    sortAndSend() {
        this.teams.sort((a, b) => a.teamName > b.teamName ? 1 : b.teamName > a.teamName ? -1 : 0);
        this.teamsListChangedEvent.next(this.teams.slice());
    }
}
