import { Component } from '@angular/core';
import { OnInit } from '@angular/core';
import { Contact } from '../contact.model';
import { ContactService } from '../contact.service';
import { ActivatedRoute, Router } from '@angular/router';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'cms-contact-edit',
  templateUrl: './contact-edit.component.html',
  styleUrls: ['./contact-edit.component.css'],
})
export class ContactEditComponent implements OnInit {
  groupContacts: Contact[] = [];
  originalContact: Contact;
  contact: Contact = null;
  editMode: boolean = false;

  constructor(
    private contactService: ContactService,
    private router: Router,
    private route: ActivatedRoute
  ) {}

  ngOnInit() {
    this.route.params.subscribe((params) => {
      let id = params['id'];

      if (id === undefined || id === null) {
        this.editMode = false;
        return;
      }

      this.contactService.getContact(id).subscribe((contact: Contact) => {
        if (contact === undefined || contact === null) {
          return;
        }

        this.originalContact = contact;
        this.editMode = true;
        this.contact = JSON.parse(JSON.stringify(contact));

        if (this.contact.group !== null) {
          this.groupContacts = JSON.parse(JSON.stringify(contact.group));
        }
      }
      );

    });
  }

  onSubmit(form: NgForm) {
    let values = form.value;
    let newContact = new Contact(
      this.contactService.getMaxId(),
      values.name,
      values.email,
      values.phone,
      values.imageUrl,
      this.groupContacts
    );

    if (this.editMode === true) {
      this.contactService.updateContact(this.originalContact, newContact);
    } else {
      this.contactService.addContact(newContact);
    }

    this.router.navigate(['/contacts']);
  }

  onCancel() {
    this.router.navigate(['/contacts']);
  }

  isInvalidContact(newContact: Contact) {
    if (!newContact) {
      return true;
    }
    if (newContact.id === this.contact.id) {
      return true;
    }
    for (let i = 0; i < this.groupContacts.length; i++) {
      if (newContact.id === this.groupContacts[i].id) {
        return true;
      }
    }
    return false;
  }

  addToGroup($event: any) {
    let selectedContact: Contact = $event.dragData;
    let invalidGroupContact =
      this.groupContacts.indexOf(selectedContact) !== -1;

    if (invalidGroupContact) {
      return;
    }

    this.groupContacts.push(selectedContact);
  }

  onRemoveItem(idx: number) {
    if (idx < 0 || idx >= this.groupContacts.length) {
      return;
    }

    this.groupContacts.splice(idx, 1);
  }
}
<div class="panel panel-default">
  <div class="panel-body">
    <form id="contact-edit" (ngSubmit)="onSubmit(f)" #f="ngForm" >       
      <div class="row">
        <div class="col-sm-12 form-group">
          <label for="name">Name:</label>
          <input
            type="text"
            id="name"
            class="form-control required"
            size="120"
            max="120"
            required 
            name="name"
            #name="ngModel"
            [ngModel]="contact?.name"
            >
        </div>
      </div>

      <div class="row">
        <div class="col-sm-12 form-group">
          <label for="email">Email:</label>
          <input
            type="text"
            id="email"
            class="form-control required"
            size="120"
            max="255"
            required
            name="email"
            #email="ngModel"
            [ngModel]="contact?.email"
            pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,3}$"
            >
            <!-- add a span to display a message to the user when the email field have not the correct patter -->
            <div *ngIf="!email.valid && email.touched" class="alert  alert-danger">
              <span >Please enter a valid email</span>
            </div>
        </div>
      </div>

      <div class="row">
        <div class="col-sm-12 form-group">
          <label for="phone">Phone:</label>
          <input
            type="text"
            id="phone"
            class="form-control invalid"
            size="150"
            max="255"
            name="phone"
            #phone="ngModel"
            [ngModel]="contact?.phone"
            pattern="\D*([2-9]\d{2})(\D*)([2-9]\d{2})(\D*)(\d{4})\D*"
            >
            <!-- add a span to display a message to the user when the phone field have not the correct patter -->
            <div *ngIf="!phone.valid && phone.touched" class="alert alert-danger">
              <span >Please enter a valid phone</span>
            </div>
        </div>
      </div>

      <div class="row">
        <div class="col-sm-12 form-group">
          <label for="imageUrl">Image URL:</label>
          <input
            type="text"
            id="imageUrl"
            class="form-control"
            size="150"
            max="255"
            name="imageUrl"
            #imageUrl="ngModel"
            [ngModel]="contact?.imageUrl"

            >
        </div>
      </div>

      <div class="row">
        <div class="form-group">
          <label for="groupList">Group Contacts:</label>

          <div class="row" id="groupList"
              style="min-height: 3rem;"
              dnd-droppable [dropZones]="['contactGroup']" (onDropSuccess)="addToGroup($event)"
              >

            <div *ngFor="let contact of groupContacts; let i = index">
              <div>
                <cms-contact-item class="col-sm-11" [contact]="contact"></cms-contact-item>
                <button class="btn btn-danger col-sm-2 deleteButton" (click)="onRemoveItem(i)">X</button>
              </div>
            </div>
            <span class="dragMsg" *ngIf="groupContacts?.length < 1">Drag contacts in group here</span>
          </div>

        </div>
      </div>


      <div class="row">
        <div class="col-xs-12 btn-toolbar">
          <button class="btn btn-success" type="submit" [disabled]="!f.valid">Save</button>
          <button class="btn btn-primary" type="button" (click)="onCancel()">Cancel</button>
        </div>
      </div>
      <div class="row">
        <div class="col-xs-12">
          <div class="alert alert-danger" *ngIf="!f.valid && f.touched">
            <div>
              <p>Please fix the following errors:</p>
              <ul>
                <li *ngIf="!name.valid">Name is required.</li>
                <li *ngIf="!email.valid">Email is required.</li>
              </ul>
            </div>
          </div>
        </div>
      </div>

    </form>
  </div>
</div>
