import { Component, OnInit } from '@angular/core';
import { Game } from '../game.model';
import { GameService } from '../game.service';
import { ActivatedRoute, Router } from '@angular/router';
import { NgForm } from '@angular/forms';
import { Team } from '../../teams/team.model';
import { TeamService } from '../../teams/team.service';

@Component({
  selector: 'wdd430-game-edit',
  templateUrl: './game-edit.component.html',
  styleUrls: ['./game-edit.component.css'],
})
export class GameEditComponent implements OnInit {
  originalGame: Game;
  game: Game = null;
  editMode: boolean = false;
  teams: void; // Variable to store the teams

  constructor(
    private gameService: GameService,
    private teamService: TeamService, // Inject the Team service
    private route: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit() {
    // Fetch the teams when the component initializes
    this.teams = this.teamService.getTeams();
    this.route.params.subscribe((params) => {
      let id = params['id'];
      if (id === undefined || id === null) {
        this.editMode = false;
        return;
      }
      this.originalGame = this.gameService.getGame(id);
      if (this.originalGame === undefined || this.originalGame === null) {
        return;
      }
      this.editMode = true;
      this.game = JSON.parse(JSON.stringify(this.originalGame));
    });
  }

  onSubmit(form: NgForm) {
    let values = form.value;
    let newGame = new Game(
      null,
      values.gameDate,
      values.time,  
      values.homeTeam,
      values.awayTeam,
      values.location,
      values.attendance,
      values.finalScore,
      values.recap
    );
    if (this.editMode === true) {
      this.gameService.updateGame(this.originalGame, newGame);
    } else {
      this.gameService.addGame(newGame);
    }
    this.router.navigate(['/games']);
  }

  onCancel() {
    this.router.navigate(['/games']);
  }
}
