import { Component, OnInit } from '@angular/core';
import { Player } from '../player.model';
import { ActivatedRoute, Router } from '@angular/router';
import { PlayerService } from '../player.service';
import { NgForm } from '@angular/forms';
import { Team } from '../../teams/team.model';
import { TeamService } from '../../teams/team.service';

@Component({
  selector: 'wdd430-player-edit',
  templateUrl: './player-edit.component.html',
  styleUrls: ['./player-edit.component.css']
})
export class PlayerEditComponent implements OnInit {
  originalPlayer: Player;
  player: Player = null;
  editMode: boolean = false;
  players: Player[]; // Variable to store the players
  teams: Team[]; // Variable to store the teams

  teamMap: { [id: number]: Team }; // Map to store teams by ID
  teamNumber: number; // Update the type to number

    constructor(
      private playerService: PlayerService,
      private route: ActivatedRoute,
      private router: Router,
      private teamService: TeamService // Inject the Team service
    ) { }

    ngOnInit() {
      this.teamService.teamListChangedEvent.subscribe((teams: Team[]) => {
        this.teams = teams;
        this.teamMap = this.createTeamMap(teams);
        console.log(this.teamMap);
      });
      this.route.params.subscribe((params) => {
        let id = params['id'];
        if (id === undefined || id === null) {
          this.editMode = false;
          return;
        }

        this.playerService.playerListChangedEvent.subscribe((players: Player[]) => {
          this.originalPlayer = this.playerService.getPlayer(id);
          if (this.originalPlayer === undefined || this.originalPlayer === null) {
            return;
          }
          this.editMode = true;
          this.player = JSON.parse(JSON.stringify(this.originalPlayer));
        });
        this.playerService.getPlayers();
      });
    }

    onSubmit(form: NgForm) {
      let values = form.value;
      let newPlayer = new Player(
        null,
        values.playerName,
        values.position,
        values.height,
        values.weight,
        values.birthDate,
        values.nationality,
        values.experienceYears,
        values.club
      );
      if (this.editMode) {
        this.playerService.updatePlayer(this.originalPlayer, newPlayer);
      } else {
        this.playerService.addPlayer(newPlayer);
      }
      this.router.navigate(['/players']);
    }

    onCancel() {
      this.router.navigate(['/players']);
    }

    private createTeamMap(teams: Team[]): { [id: number]: Team } {


}
