      <h4 class="title margin-left-right">{{document?.name}}</h4>
      <div class="row pull-right margin-left-right">
        <a class="btn btn-primary">View</a>
        <a class="btn btn-info" >Edit</a>
        <a class="btn btn-danger">Delete</a>
      </div>
    </div>
  </div>
  <div class="panel-body">
    <div class="pad-left-right">
      <!-- <div class="row">
        <label>Description:</label>
        <span class="label-value">{{document?.description}}</span>
      </div>
      <br> -->
      <div class="row">
        <label>URL:</label>
        <span class="label-value">{{document?.url}}</span>
      </div>
    </div>
  </div>
</div>
