import { Component } from '@angular/core';
import { Message } from '../message.model';

@Component({
  selector: 'cms-message-list',
  templateUrl: './message-list.component.html',
  styleUrls: ['./message-list.component.css']
})
export class MessageListComponent {

  messages = [
    new Message(1, 'A Message', 'This is a message', 'Rony Calderon'),
    new Message(2, 'Another Message', 'This is another message', 'Rony Calderon'),
    new Message(3, 'Yet Another Message', 'This is yet another message', 'Rony Calderon')
  ];

  onAddMessage(message: Message) {
    this.messages.push(message);
  }

    constructor() { }



}
<div class="row">
  <div class="col-md-5">
    <div class="panel panel-default">
      <div class="panel-heading">
        <div class="row pad-left-right">
          <span class="title pull-left">Messages</span>
        </div>
      </div>
      <div class="panel-body">
        <div class="row">
          <div class="col-xs-12">
            <cms-message-item
            *ngFor="let message of messages"
            ></cms-message-item>
          </div>
        </div>
      </div>
      <div class="panel-footer">
        <cms-message-edit
        ()
        ></cms-message-edit>
      </div>
    </div>
  </div>
</div>
